Query.Sql:
with CT as ( select distinct Record ->> '$.LinkagePatientId.NhsNumber.@extension' as NhsNumber,
unnest( [ [Record ->> '$.Treatment.Surgery.ProcedureDate'], Record ->> '$.Treatment[*].Surgery.ProcedureDate' ], recursive := true ) as ProcedureDate,
unnest( [ [Record ->> '$.Treatment.Surgery.ProcedureOpcs.@code'], Record ->> '$.Treatment[*].Surgery.ProcedureOpcs[*].@code' ], recursive := true ) as ProcedureOpcsCode from omop_staging.cosd_staging_901 where type = 'CT' ) select distinct NhsNumber, ProcedureDate, ProcedureOpcsCode from CT where ProcedureOpcsCode is not null;
